import React from 'react';
import { useLocation } from 'react-router-dom';

const BookingForm = () => {
  // Use useLocation hook to get state from the router
  const location = useLocation();
  const flightDetails = location.state?.flightDetails || {};

  return (
    <div>
      <h2>Booking Form</h2>
      {/* Display the flight details */}
      <div>
        <p>Weekday: {flightDetails.weekday}</p>
        <p>Departure: {flightDetails.departure}</p>
        <p>Arrival: {flightDetails.arrival}</p>
        <p>Aircraft: {flightDetails.aircraft}</p>
        {/* Add more details as needed */}
      </div>
      {/* Your existing booking form code */}
    </div>
  );
};

export default BookingForm;
