import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ selectedBooking }) => {
  const [paymentData, setPaymentData] = useState({
    cardHolderName: '',
    cardNumber: '',
    expirationDate: '',
    cvv: '',
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPaymentData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const validateCreditCard = () => {
    const cardNumberRegex = /^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13}|6(?:011|5[0-9]{2})[0-9]{12})$/;

    const isCardValid = cardNumberRegex.test(paymentData.cardNumber);
    const isExpirationDateValid = validateExpirationDate(paymentData.expirationDate);
    const isCVVValid = /^\d{3,4}$/.test(paymentData.cvv);

    const newErrors = {};

    if (!isCardValid) {
      newErrors.cardNumber = 'Invalid card number';
    }

    if (!isExpirationDateValid) {
      newErrors.expirationDate = 'Invalid expiration date';
    }

    if (!isCVVValid) {
      newErrors.cvv = 'Invalid CVV';
    }

    setErrors(newErrors);

    return isCardValid && isExpirationDateValid && isCVVValid;
  };

  const validateExpirationDate = (date) => {
    // Basic check for MM/YYYY format
    const expirationDateRegex = /^(0[1-9]|1[0-2])\/[0-9]{4}$/;
    return expirationDateRegex.test(date);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateCreditCard()) {
      try {
        // Save the payment data to the backend
        await axios.post('http://localhost:3001/api/payments', {
          ...paymentData,
          bookingId: selectedBooking.id, // Assuming you have a unique identifier for the booking
        });

        // Proceed to the next page (you can customize this part)
        // For now, let's just log a message
        console.log('Payment successful. Proceeding to the next step.');
      } catch (error) {
        console.error('Error processing payment:', error);
        // Handle error, show a message, etc.
      }
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Payment Information</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="cardHolderName" className="form-label">
            Card Holder Name
          </label>
          <input
            type="text"
            className="form-control"
            id="cardHolderName"
            name="cardHolderName"
            value={paymentData.cardHolderName}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="cardNumber" className="form-label">
            Card Number
          </label>
          <input
            type="text"
            className={`form-control ${errors.cardNumber ? 'is-invalid' : ''}`}
            id="cardNumber"
            name="cardNumber"
            value={paymentData.cardNumber}
            onChange={handleInputChange}
            required
          />
          {errors.cardNumber && <div className="invalid-feedback">{errors.cardNumber}</div>}
        </div>
        <div className="mb-3">
          <label htmlFor="expirationDate" className="form-label">
            Expiration Date (MM/YYYY)
          </label>
          <input
            type="text"
            className={`form-control ${errors.expirationDate ? 'is-invalid' : ''}`}
            id="expirationDate"
            name="expirationDate"
            value={paymentData.expirationDate}
            onChange={handleInputChange}
            required
          />
          {errors.expirationDate && <div className="invalid-feedback">{errors.expirationDate}</div>}
        </div>
        <div className="mb-3">
          <label htmlFor="cvv" className="form-label">
            CVV
          </label>
          <input
            type="text"
            className={`form-control ${errors.cvv ? 'is-invalid' : ''}`}
            id="cvv"
            name="cvv"
            value={paymentData.cvv}
            onChange={handleInputChange}
            required
          />
          {errors.cvv && <div className="invalid-feedback">{errors.cvv}</div>}
        </div>
        <button type="submit" className="btn btn-primary">
          Submit Payment
        </button>
      </form>
    </div>
  );
};

export default PaymentForm;
