// BookingForm.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingForm = () => {
  const [selectedBooking, setSelectedBooking] = useState(null);

  // Fetch additional details for the selected booking if needed
  useEffect(() => {
    const fetchBookingDetails = async () => {
      if (selectedBooking) {
        try {
          const response = await axios.get(
            `http://localhost:3001/api/bookings/${selectedBooking._id}`
          );
          // Assuming the API endpoint returns additional booking details
          const bookingDetails = response.data;
          // Update the selected booking with additional details
          setSelectedBooking({ ...selectedBooking, ...bookingDetails });
        } catch (error) {
          console.error('Error fetching booking details:', error);
        }
      }
    };

    fetchBookingDetails();
  }, [selectedBooking]);

  return (
    <div>
      <h2>Booking Form</h2>
      {/* Display the selected booking details */}
      {selectedBooking && (
        <div>
          <p>Selected Flight Details:</p>
          <p>Flight ID: {selectedBooking.flightId}</p>
          <p>User ID: {selectedBooking.userId}</p>
          {/* Display additional booking details as needed */}
        </div>
      )}
      {/* ... (existing code) */}
    </div>
  );
};

export default BookingForm;
