import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = ({ selectedBooking }) => {
  const [paymentData, setPaymentData] = useState({
    cardHolderName: '',
    cardNumber: '',
    expirationDate: '',
    cvv: '',
  });

  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPaymentData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const generateRandomCardNumber = () => {
    // Generate a random 16-digit card number for testing
    const randomCardNumber = Array.from({ length: 16 }, () => Math.floor(Math.random() * 10)).join('');
    setPaymentData((prevData) => ({
      ...prevData,
      cardNumber: randomCardNumber,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Save the payment data to the backend
      await axios.post('http://localhost:3001/api/payments', {
        ...paymentData,
        bookingId: selectedBooking.id, // Assuming you have a unique identifier for the booking
      });

      // Update state to indicate successful payment
      setPaymentSuccess(true);

      // Log a message
      console.log('Payment successful. Proceeding to the next step.');
    } catch (error) {
      console.error('Error processing payment:', error);
      // Handle error, show a message, etc.
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Payment Information</h1>
      <form onSubmit={handleSubmit}>
        {/* Your input fields here */}
        <div className="mb-3">
          <button type="button" className="btn btn-secondary" onClick={generateRandomCardNumber}>
            Generate Random Card Number
          </button>
        </div>
        {/* Rest of the form */}
        <button type="submit" className="btn btn-primary">
          Submit Payment
        </button>
      </form>

      {paymentSuccess && (
        <div className="alert alert-success mt-3" role="alert">
          Payment successful!
        </div>
      )}
    </div>
  );
};

export default PaymentForm;
