// BookingForm.js

import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = ({ flightDetails, onBookingSubmit }) => {
  const [passengerName, setPassengerName] = useState('');
  const [email, setEmail] = useState('');

  const handleBookingSubmit = async (e) => {
    e.preventDefault();

    try {
      // Assuming you have a backend endpoint to handle booking submission
      const response = await axios.post('/api/bookings', {
        flightDetails,
        passengerName,
        email,
      });

      // Assuming the backend responds with the newly created booking
      const newBooking = response.data;

      // Pass the new booking to the parent component
      onBookingSubmit(newBooking);
    } catch (error) {
      console.error('Error submitting booking:', error);
    }
  };

  return (
    <div>
      <h2>Booking Form</h2>
      <form onSubmit={handleBookingSubmit}>
        <label>
          Passenger Name:
          <input type="text" value={passengerName} onChange={(e) => setPassengerName(e.target.value)} required />
        </label>
        <br />
        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </label>
        <br />
        <button type="submit">Submit Booking</button>
      </form>
    </div>
  );
};

export default BookingForm;
